    public static List<List<Integer>> threeSum(int[] nums) {
        Arrays.sort(nums);
        List<List<Integer>> triplet = new ArrayList<>();
        for (int counter = 0; counter < nums.length-2; counter++){
            if (nums[counter] > 0) {
                break;
            }
            if(counter == 0 || nums[counter] != nums[counter-1]){
                int leftIndex = counter+1;
                int rightIndex = nums.length-1;
                while (leftIndex < rightIndex){
                    int subTotal = nums[counter] + nums[leftIndex];
                    if(Math.abs(subTotal) >= nums[rightIndex]){
                        int target = subTotal+nums[rightIndex];
                        if(target == 0) {
                            List<Integer> listOfIntegers = new ArrayList<>(Arrays.asList(nums[counter], nums[leftIndex], nums[rightIndex]));
                            triplet.add(listOfIntegers);
                        }
                        while (nums[++leftIndex] == nums[leftIndex-1] ){
                            if(leftIndex >= rightIndex) {
                                break;
                            }
                        }
                    }else {
                        while (nums[--rightIndex] == nums[rightIndex+1] ){
                            if(leftIndex >= rightIndex) {
                                break;
                            }
                        }

                    }
                }
            }
        }
        return triplet;
    }
