package comp_p.hubspot;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

class MinimumWindowSubstringTest {

    @Test
    void testMinWindow() {
        String s = "ADOBECODEBANC";
        String t = "ABC";
       assertEquals("BANC", MinimumWindowSubstring.minWindow(s,t));

        String s2 = "a";
        String t2 = "a";
        assertEquals("a", MinimumWindowSubstring.minWindow(s2,t2));

        String s3 = "a";
        String t3 = "aa";
        assertEquals("", MinimumWindowSubstring.minWindow(s3,t3));

        String s4 = "";
        String t4 = "";

        String result4 = "";
        assertEquals(result4, MinimumWindowSubstring.minWindow(s4,t4));

        String s5 = "a";
        String t5 = "b";
        assertEquals("", MinimumWindowSubstring.minWindow(s5,t5));
    }
}